INSERT INTO `boxes` VALUES(2, 'Art Shanty Projects is an artist driven temporary community exploring the ways in which the relatively unregulated public space of the frozen lake can be used as a new and challenging artistic environment to expand notions of what art can be.  <a href="http://www.artshantyprojects.org/mission" title="Art Shanty Project website">Find out more..</a>', 'Art Shanty Project', 1);
INSERT INTO `boxes` VALUES(3, 'The Map Shanty Project is a collaborated effort of <a href="/mappers" title="Radical Mappers of the Radical Mapping Art Shanty Project">many people</a>.  We also have many <a href="/og" title="Map Shanty Working Groups">working groups</a>.  This website is an open space for creating the Radical Mapping Art Shanty Project.', 'Collaboration Block', 1);
INSERT INTO `boxes` VALUES(4, 'The Map Shanty Project is actually an <a href="http://www.excotc.org/node/266" title="Experimental College Twin Cities">ExCo Class</a>.  Feel free to sign up, though most of the collaboration will happen here.', 'ExCo Block', 1);
