INSERT INTO `boxes` VALUES(6, '<div class="schedule-block-block">\r\n\r\n<div class="schedule-block">\r\n<h3 class="schedule-title">Transportation and Locomotion</h3>\r\n<div class="schedule-date">Saturday, January 17 at 1:00 PM</div>\r\n<div class="schedule-date">Sunday, January 18 at 1:00 PM</div>\r\n<p class="schedule description">Mapping Party about Transportation and Locomotion.</p>\r\n</div>\r\n\r\n<div class="schedule-block">\r\n<h3 class="schedule-title">Map the Shanty Town</h3>\r\n<div class="schedule-date">Saturday, January 24 at 1:00 PM</div>\r\n<p class="schedule description">Bring your GPS or just come ready to put this town on a map!</p>\r\n</div>\r\n\r\n<div class="schedule-block">\r\n<h3 class="schedule-title">Sound of the Shanties</h3>\r\n<div class="schedule-date">Sunday, January 25 at 1:00 PM</div>\r\n<p class="schedule description">Mapping sound on Medicine Lake.</p>\r\n</div>\r\n\r\n<div class="schedule-block">\r\n<h3 class="schedule-title">History of Medicine Lake</h3>\r\n<div class="schedule-date">Saturday, January 31 at 1:00 PM</div>\r\n<p class="schedule description">Lecture on the History of Medicine Lake in Maps.</p>\r\n</div>\r\n\r\n<div class="schedule-block">\r\n<h3 class="schedule-title">Maps and Your History</h3>\r\n<div class="schedule-date">Sunday, February 01 at 1:00 PM</div>\r\n<p class="schedule description">Map your own history.</p>\r\n</div>\r\n\r\n<div class="schedule-block">\r\n<h3 class="schedule-title">Food</h3>\r\n<div class="schedule-date">Saturday, February 07 at 1:00 PM</div>\r\n<div class="schedule-date">Sunday, February 08 at 1:00 PM</div>\r\n<p class="schedule description">Map your favorite food.</p>\r\n</div>\r\n\r\n<div class="schedule-block">\r\n<h3 class="schedule-title">Re-Mapping and Closing</h3>\r\n<div class="schedule-date">Saturday, February 14 at 1:00 PM</div>\r\n<p class="schedule description">Snowtracks, ice cracks, and chilly romance: re-mapping the shanties, post-settlement.</p>\r\n</div>\r\n\r\n</div>', 'Schedule for Front Page', 2);
