INSERT INTO `node_revisions` VALUES(41, 76, 14, '11/16 Meeting @ 4pm - Bedlam', 'Map Shanty\r\nSunday 11/16 4pm \r\n@ Bedlam\r\n\r\nHey everyone! Same time, same place this week.  Here is a likely agenda:\r\n\r\n-- Overview of last Sunday''s Art Shanty Project meeting\r\n-- Update on our scavenging/shopping at Theater de la Juene Lune\r\n-- Timelines for construction, scheduling\r\n-- Shanty design\r\n-- Ongoing Projects\r\n\r\n\r\nREMINDERS:\r\nPlease create a profile with a picture on www.mapshanty.com! My laptop and webcam will be available tomorrow during class if you need help or haven''t had the time to do this yet!\r\n\r\nBring your calendar to discuss December and early January availability\r\n\r\nThink about supply requests and estimated costs for activities & construction\r\n\r\nBrowse through this flickr set to see an example of how one shanty was put together -- http://www.flickr.com/photos/phaakon/741176429/in/photostream/\r\n\r\nIf you''re still in the mood, take a look at these shanty photos: http://www.flickr.com/search/?s=int&w=all&q=art+shanty+project&m=tags', 'Map Shanty\r\nSunday 11/16 4pm \r\n@ Bedlam\r\n\r\nHey everyone! Same time, same place this week.  Here is a likely agenda:\r\n\r\n-- Overview of last Sunday''s Art Shanty Project meeting\r\n-- Update on our scavenging/shopping at Theater de la Juene Lune\r\n-- Timelines for construction, scheduling\r\n-- Shanty design\r\n-- Ongoing Projects\r\n\r\n\r\nREMINDERS:\r\nPlease create a profile with a picture on www.mapshanty.com! My laptop and webcam will be available tomorrow during class if you need help or haven''t had the time to do this yet!\r\n\r\nBring your calendar to discuss December and early January availability\r\n\r', '', 1226808618, 1);
