INSERT INTO `node_revisions` VALUES(136, 202, 1, 'IAMER Research Lab', 'After the success of the ASP2008 Field Museum, IAMER will install a Research Lab on Medicine Lake. We plan to perform a more intense and comprehensive study of the social, geographical, and environmental aspects of the Art Shanty community. Our research will include weekly activities such as: kite aerial photography, a collaborative community music improvisation workshop, documentation of local folklore, and various research "games." Our Research Lab will also be equipped with a research library available for public use as well as various ongoing sensory perception experiments.', 'After the success of the ASP2008 Field Museum, IAMER will install a Research Lab on Medicine Lake. We plan to perform a more intense and comprehensive study of the social, geographical, and environmental aspects of the Art Shanty community. Our research will include weekly activities such as: kite aerial photography, a collaborative community music improvisation workshop, documentation of local folklore, and various research "games." Our Research Lab will also be equipped with a research library available for public use as well as various ongoing sensory perception experiments.', '', 1232329824, 1);
INSERT INTO `node_revisions` VALUES(137, 203, 1, 'Art Post Shanty', 'The world''s only Post Office on ice, the Art Post Shanty is the official mail service for the Art Shanty Project. Make a postcard to send from the ice OR send us mail that we''ll deliver to your favorite Art Shanty. Join us for wacky projects and performances, including "Art on Ice," the first mail art show held in a shanty!', 'The world''s only Post Office on ice, the Art Post Shanty is the official mail service for the Art Shanty Project. Make a postcard to send from the ice OR send us mail that we''ll deliver to your favorite Art Shanty. Join us for wacky projects and performances, including "Art on Ice," the first mail art show held in a shanty!', '', 1232329985, 1);
INSERT INTO `node_revisions` VALUES(138, 204, 1, 'Norae Bang Shanty', 'The Norae Bang Shanty brings the love of karaoke out onto the ice. A warm hate-free zone provides an outlet for you to pop in and share your karaoke capabilities or a chance to sing one of your favorite songs.', 'The Norae Bang Shanty brings the love of karaoke out onto the ice. A warm hate-free zone provides an outlet for you to pop in and share your karaoke capabilities or a chance to sing one of your favorite songs.', '', 1232330293, 1);
INSERT INTO `node_revisions` VALUES(139, 205, 1, 'Knitting Shanty', 'This is our 4th year with the ASP. We create warm art and cozy knit goods. Drop in and see what we are working on. Bring your own knitting project and share stories with us. Or if you don''t knit, ask nicely and we may show you a stitch or two!', 'This is our 4th year with the ASP. We create warm art and cozy knit goods. Drop in and see what we are working on. Bring your own knitting project and share stories with us. Or if you don''t knit, ask nicely and we may show you a stitch or two!', '', 1232330492, 1);
INSERT INTO `node_revisions` VALUES(140, 206, 1, 'Ship in the Bottle Shanty', 'Artists with no discipline are pleased to bring you the Ship in the Bottle Shanty. Its purpose is to take sail to icehouse, create wonder, and bring people together in a zone where arctic barn raising is rewarded with the comrade ship of the sea. Join our motley crew turning 16ft bottle shanty to 32ft ice ship, and take to the high ice.', 'Artists with no discipline are pleased to bring you the Ship in the Bottle Shanty. Its purpose is to take sail to icehouse, create wonder, and bring people together in a zone where arctic barn raising is rewarded with the comrade ship of the sea. Join our motley crew turning 16ft bottle shanty to 32ft ice ship, and take to the high ice.', '', 1232330633, 1);
