INSERT INTO `node_type` VALUES('rad_mapper_profile', 'Radical Mapper Profile', 'node', 'As part of the Radical Mapping Art Shanty team this is your profile.  ', 'This is your Radical Mapping Art Shanty Profile.  Make it fun!  Make sure to create a Favorite Place.  <br />\r\nTo create a Favorite Place: <br />\r\n(1) Choose the point of your Favorite Place by going to the Location box and clicking on the map.  You can put in an address, but this is not necessary.<br />\r\n(2) Choose how your map will look by going to the Favorite Place box and using a GMap Macro.<br />\r\n(3) Write a Description of your Favorite Place.', 1, 'Name', 1, 'Description/Bio', 10, 1, 1, 0, 'profile');
